class UfxInclude extends HTMLElement{async fetchHtml(t){let e=await fetch(t);if(!e.ok)throw new Error(`ufx-include failed to fetch ${t}: Response status: ${e.status}`);const s=await e.text();this.innerHTML=s;const i=Array.from(this.childNodes);i.forEach(t=>{this.parentElement&&this.parentElement.insertBefore(t,this)}),this.remove()}connectedCallback(){const t=this.getAttribute("src");this.fetchHtml(t)}}customElements.define("ufx-include",UfxInclude);class UfxLinkInclude extends HTMLElement{constructor(){super(),this.addEventListener("click",t=>{this.loadTarget()})}async fetcHtml(){let t=await fetch(this.src);if(!t.ok)throw new Error(`ufx-include failed to fetch ${this.src}: Response status: ${t.status}`);const e=await t.text();this.hiddenTargetElement=document.createElement("div"),this.hiddenTargetElement.innerHTML=e,this.nodes=Array.from(this.hiddenTargetElement.childNodes)}loadTarget(){if(!this.src||!this.targetId)return;const t=document.getElementById(this.targetId);for(;t.firstChild;)t.removeChild(t.firstChild);this.nodes.forEach(e=>{t.appendChild(e)})}connectedCallback(){this.src=this.getAttribute("src"),this.targetId=this.getAttribute("target-id"),this.src&&this.targetId&&this.fetcHtml()}}customElements.define("ufx-link-include",UfxLinkInclude);